import SideBar from "./SideBar"
import Post from "./Post";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Home(){
    const [submit, setSubmit] = useState(false);

    
    
    return(
        <div className = "home">
            <button className = "bttn" type="button" onClick={() => setSubmit(true)}> click me </button>
            {submit ? <Post /> : null}
            
        </div>
    )
}

export function Smit(){
    setSubmit(true);
}

export function CreatePost(){
    const nav = useNavigate();
    
    return(
        <div className="cWrapper">
            <form onSubmit={Smit}>
                <label id="chooseFile"> <strong> Upload Media </strong> <br/><br/></label>
                <input type="file" id = "file" name="first" ></input><br/><br/>

                <label id="chooseFile"> <strong> Caption </strong> <br/><br/></label>

                <input type="text" id = "username" name="first" required></input> <br/><br/>
                <input type="submit" id = "submit" name="submit"></input>
            </form>
        </div>
    )
}
