import React from "react";
import ReactDOM from "react-dom";
import SignIn from "./SignIn"

import { BrowserRouter, Routes, Route } from "react-router-dom";
import Layout from "./layout";

function SideBar(){
    return (
        <div className = "afeedContainer">
            <div className = "afeed">
                <ul className = "aList">
                    <li><a href="#About_Me"> <br></br>Followers &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</a></li><br></br>
                    <li><a href="#About_Me"> Following &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</a></li><br></br>
                    <li><a href="#About_Me"> Create Post &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</a></li><br></br>
                </ul>
            </div>
            <button className="btn" type="button" onClick={BarAnimation}> 
                <img className="arrow" src= "arrow.png" width="40px" height="25px"></img> 
                <img hidden className ="arrow2" src= "arrow2.png" width="40px" height="25px"></img> 
            </button>
        </div>
    )
}

function BarAnimation(){
    const afeed = document.querySelector(".afeed");
    afeed.classList.toggle("hidden");
    const arrow = document.querySelector(".arrow");
    arrow.toggleAttribute("hidden");
    const arrow2 = document.querySelector(".arrow2");
    arrow2.toggleAttribute("hidden");
    const aList = document.querySelector(".aList");
    aList.classList.toggle("hidden2");
    const hdBtn = document.querySelector(".btn");
    hdBtn.classList.toggle("hiddenBtn");
    //const sideList = document.querySelector(".sidelist");
    //sideList.toggleAttribute("hidden");
}


function Footer(){
    return(
        <footer className = "foot">
            &copy; 2022 OMCT Development Team. All rights reserved.
        </footer>
    )
}

function Page(){
    return(
        
            <BrowserRouter>
                <Routes>
                    <Route path = "/" element = {<Layout/>}>
                        <Route index element = {<Page />}/>
                        <Route path = '/sign-in' element = {<SignIn/>}/>
                    </Route>
                </Routes>
            </BrowserRouter>
        
    )
}


ReactDOM.render(<Page/>, document.getElementById("root"))
